import data from "@/utilities/data.js";
import LeftNav from "@/components/index/LeftNav";
import TopBar from "@/components/index/TopBar";
import MsgDialog from "@/components/index/MsgDialog"; // import OpenDialog from "@/components/OpenDialog";

export default {
  components: {
    LeftNav: LeftNav,
    TopBar: TopBar,
    MsgDialog: MsgDialog
  },
  data: function data() {
    return {
      isShowNav: false,
      isMaskshow: false,
      isRouterAlive: true,
      loading: 1,
      loadNum: 0,
      loadpercent: 0
    };
  },
  created: function created() {
    var _this = this;

    this.$bus.on("isShowNav", function (val) {
      _this.isShowNav = val;
    });
  },
  mounted: function mounted() {
    this.onLoading();
  },
  methods: {
    onLoading: function onLoading() {
      var _this2 = this;

      var obj = data;
      var list = JSON.parse(JSON.stringify(obj));
      var picarr = [];
      var href = "";

      var getArr = function getArr(arr) {
        var img = [];

        for (var i = 0, l = arr.length; i < l; i++) {
          img.push(setUrl(arr[i].url));
        }
      };

      var setUrl = function setUrl(url) {
        var img = "";

        if (url.indexOf("http") > -1) {
          img = url;
        } else {
          img = href + _this2.baseurl + url;
        }

        return img;
      };

      for (var i in list) {
        if (list.hasOwnProperty(i)) {
          var _obj = Object.prototype.toString.call(list[i]);

          if (_obj === "[object Array]") {
            for (var j = 0, l = list[i].length; j < l; j++) {
              var imgurl = list[i][j];

              if (imgurl.url) {
                picarr.push(setUrl(imgurl.url));
              }
            }
          } else {
            if (list[i].face) {
              picarr = picarr.concat(getArr(list[i].face));
            }
          }
        }
      }

      this.laodFull = picarr.length;
      this.getLoadImg(picarr);
      return picarr;
    },
    getLoadImg: function getLoadImg(arr) {
      var _this3 = this;

      var onloadarr = []; // console.log(arr)

      var backFun = function backFun() {
        _this3.loadNum += 1;
        _this3.loadpercent = _this3.loadNum / arr.length * 100; // console.log(this.loadNum, arr.length, this.loadNum / arr.length, 1 / 2)

        if (_this3.loadNum === arr.length - 1) {
          // console.log('11111')
          _this3.mytime = setTimeout(function () {
            clearTimeout(_this3.mytime);
            _this3.loading = 0;
          }, 500);
        }
      };

      for (var i = 0; i < arr.length; i++) {
        onloadarr[i] = new Image();

        onloadarr[i].onload = function () {
          // console.log(i, arr[i])
          backFun();
        };

        onloadarr[i].src = arr[i];
      }
    }
  } // updated() {
  //   window.scroll(0, 0);
  // }

};
