import dataM from "@/utilities/menu";
export default {
  name: "LeftNav",
  data() {
    return {
      dataM: dataM,
      activeLink: false,
      isCollapse: true,
      fullWidth: 0,
      isTxtShow: false,
      toCurrent: "",
      isShowNav: false //小尺寸show
    };
  },
  created() {
    this.$bus.on("isShowNav", val => {
      this.isShowNav = val;
    });
    this.$bus.on("isTxtShow", val => {
      this.isTxtShow = val;
    });
  },
  mounted() {
    this.handleScreen();
  },
  methods: {
    handleScreen() {
      //偵測螢幕寬度
      this.fullWidth = window.innerWidth;
      window.onresize = () => {
        this.fullWidth = window.innerWidth;
      };
      if (this.fullWidth > 1200 || this.fullWidth < 576) {
        this.isCollapse = false;
      } else {
        this.isCollapse = true;
      }
    },
    handleShowtxt() {
      //判斷平板秀出文字
      if (this.isCollapse) {
        this.isTxtShow = true;
      }
      this.$emit("showtxt", this.isTxtShow);
    },
    handleMenu() {
      //判斷手機顯示menu
      this.isShowNav = !this.isShowNav;
      this.$bus.emit("isShowNav", this.openMenu);
    },
    handleCurrent(index) {
      //麵包屑功能
      this.toCurrent = dataM[index].title;
      this.$bus.emit("linkCurrent", this.toCurrent);
    },
    handleCloseMenu() {
      if (this.isShowNav) {
        this.isShowNav = false;
        this.$bus.emit("isShowNav", this.openMenu);
      }
    },
    handleCloseTxt() {
      if (this.isCollapse) {
        this.isTxtShow = false;
      }
      // this.$bus.emit("isMaskshow", this.isTxtShow);
      this.$bus.emit("isTxtShow", this.isMaskshow);
    }
  }
};
