export default {
  data() {
    return {
      ChangName: ""
    };
  },
  methods: {
    handleSave() {
      if (this.ChangName !== null && this.ChangName !== "") {
        let Msg = {
          isShow: true,
          content: "修改成功囉",
          link: "index"
        };
        localStorage.setItem("name", this.ChangName);
        this.$bus.emit("name2", this.ChangName);
        this.$bus.emit("Msg", Msg);
      } else {
        let Msg = {
          isShow: true,
          content: "不能不填寫喔",
          link: ""
        };
        this.$bus.emit("Msg", Msg);
      }
    },
    handleCancel() {
      this.$router.push("index");
    }
  }
};
