.About-box {
  margin-top: 3vw;
  padding: 3vw 5vw;
  .avatar-box {
    border-radius: 30vw;
    padding: 4vw;
    img {
      width: 8vw;
      height: 8vw;
      border-radius: 30vw;
      background-size: contain;
    }
    span {
      display: inline-block;
      margin-top: 2vw;
      font-weight: bold;
      font-size: 24px;
    }
  }
  .contact-list {
    margin-top: 2vw;
    height: 15vw;
    font-size: 20px;
    .flex-pos {
      height: 100%;
      span {
        margin-left: 10px;
        font-weight: bold;
        text-decoration: underline;
      }
    }
  }
