@media (max-width: 576px) {
  .About-box {
    margin-top: 20vw;
    .shadow-box .title-area {
      height: 10vw;
      line-height: 10vw;
      padding-left: 3vw;
    }
    .works-area {
      min-height: 78vw;
    }
    .avatar-box {
      img {
        width: 25vw;
        height: 25vw;
      }
      span {
        display: block;
        margin-top: 6vw;
      }
    }
    .contact-list .flex-pos span {
      display: contents;
    }
  }
}
