result:
.txt-area {
  color: #001c49;
  font-size: 22px;
  width: 100%;
  height: calc(100% - 10vw);
  .flex-pos {
    height: 100%;
    .word-area {
      font-size: 24px;
      &:before {
        content: "歡迎來參觀";
      }
    }
  }
}
//iphone
@media only screen and (max-width: 414px) {
  .txt-area {
    height: calc(100% - 40vw);
    overflow: hidden;
    .flex-pos {
      .word-area {
        width: 80%;
        font-size: 14px;
        &:before {
          content: "歡迎來參觀";
        }
      }
    }
  }
}
