.VueWorks-box {
  margin-top: 3vw;
  padding: 3vw 5vw;
  .shadow-box {
    margin-top: 2vw;
  }
  .works-area {
    padding: 2vw;
    .works-photo {
      width: 12vw;
      max-height: 25vw;
      background: #555;
      border-radius: 1vw;
      //   overflow: hidden;
      margin-left: 2vw;
      cursor: pointer;
      &.lateral {
        width: 15vw;
        margin-left: 0;
        margin-top: 3vw;
      }
      img {
        width: 100%;
        height: auto;
        border-radius: 1vw;
        box-shadow: 0 0 10px 3px rgba(248, 248, 248, 0.5);
        ï¼†:hover {
          opacity: 0.8;
          background: #fff;
        }
      }
    }
    .works-list {
      text-align: left;
      .txt {
        cursor: pointer;
        font-weight: bold;
        text-decoration: underline;
      }
      dl {
        margin-top: 5vw;
      }
      dt,
      dd {
        margin-bottom: 0.8vw;
      }
      span {
        font-weight: bold;
      }
    }
  }
}
@media (max-width: 576px) {
  .VueWorks-box {
    margin-top: 15vw;
    .shadow-box {
      margin-top: 5vw;
    }
    .works-area {
      padding: 4vw;
      .works-photo {
        width: 15vw;
        height: auto;
        border-radius: 2vw;
        &.lateral {
          width: 40vw;
        }
        img {
          width: 100%;
          height: auto;
        }
      }
      .works-list {
        margin-top: 3vw;
      }
    }
  }
}
