@import "./scss/index.scss";
#app {
  font-family: "Avenir", Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: $darkestBlack;
  width: 100%;
  height: 100%;
  overflow: hidden;
  position: relative;
  .el-container {
    height: 100%;
  }
}
#nav {
  padding: 30px;
  a {
    font-weight: bold;
    color: #2c3e50;
    &.router-link-exact-active {
      color: #42b983;
    }
  }
}
.main-box {
  // overflow: hidden !important;
  // position: fixed;
  // width: 100%;
  position: relative;
  left: 0;
  height: 100%;
  .content-box {
    height: calc(100% - 4vw);
    .content-txt {
      padding: 1.5vw;
    }
  }
}
.back-phone {
  display: none !important;
  position: fixed;
  left: 10px;
  top: 10px;
  height: 10vw;
  color: #fff;
  text-align: center;
  z-index: 99999;
  &.show {
    display: none !important;
  }
}
.mask-box {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.6);
}
.load-box {
  position: absolute;
  top: 0;
  background: #000;
  // opacity: .8;
  width: 100%;
  height: 100%;
  z-index: 9999;
  .flex-box {
    height: 100%;
    .flex {
      height: 100%;
      p {
        font-weight: bold;
        color: #fff;
        font-size: 30px;
      }
      i {
        color: #fff;
        font-size: 50px;
      }
    }
  }
}
@media (max-width: 576px) {
  .back-phone {
    &.show {
      display: block !important;
    }
  }
  .main-box {
    // overflow: hidden !important;
    position: fixed;
    width: 100%;
    height: 100%;
    .content-box {
      height: calc(100% - 4vw);
      .content-txt {
        padding: 1.5vw;
      }
    }
  }
}
